insert into Therapy (kind_of_therapy, description, price) values ('Konsultacja psychologiczna', 'Konsultacje psychologiczne są pierwszym etapem pomocy psychologicznej, której zasadniczym celem jest rozpoznanie, uściślenie i omówienie głównych obszarów problemowych. Często są wstępem do psychoterapii indywidualnej', 150);
insert into Therapy (kind_of_therapy, description, price) values ('Psychoterapia indywidualna', 'Psychoterapia jest specjalistyczną metodą leczenia różnych zaburzeń psychicznych, a także drogą rozwoju i zwiększania świadomości samego siebie. W odróżnieniu od pozostałych form wsparcia psychicznego, proces psychoterapii opiera się na świadomych i planowanych działaniach ze strony psychoterapeuty, który posiada profesjonalną wiedzę i doświadczenie kliniczne. Istnieje możliwość terapii online.', 150);
insert into Therapy (kind_of_therapy, description, price) values ('Psychoterapia grupowa', 'Grupa terapeutyczna poprzez odzwierciedlenie, może pokazać również sposoby tworzenia
insert into Therapy (kind_of_therapy, description, price) values ('Terapia par', 'Terapia par skierowana jest dla osób przechodzących kryzys małżeński lub partnerski, potrzebujących poprawy wzajemnej komunikacji; mediacje mające na celu ustalenie rozwiązań przy dbaniu o korzyści dla obu stron', 200);
insert into Therapy (kind_of_therapy, description, price) values ('Wykład', 'Poprowadzenie wykładu lub warsztatów o określonej tematyce z zakresu psychologii, chorób psychicznych, uzależnień, treningu umiejętności interpersonalnych.', 400);
